// This is some dangerous functions here. Does not check for conditions
// x: all real numbeers
// y: all real numbers
// Pass two coordinate objects
// {
// x:real numbeer
// y:real number
// }
export function calculate_distance(coordinate1, coordinate2)
{
	//Use pythagorean theorem to solve for the hypotetnuse
	deltax = coordinate1.x - coordinate2.x
	deltay = coordinate1.y - coordinate2.y
	distance = Math.sqrt(Math.pow(deltax, 2) + Math.pow(deltay, 2))
	return distance
}
